import React, { Component } from 'react';
import {imageArr} from '../constants'

export default class WeatherReport extends Component {
    // <div>
    //     <h1>Hello World</h1>
    // </div>
    render() {
        const { data} = this.props;
        console.log('11111', data)
        return (
            <div style={styles.mainDiv}>
        <div style={{
            display: 'flex', 
            flexDirection: 'row',
        }}>
        <h1 style={{fontWeight: 'bold'}}>{data.maxTemp}&deg; C</h1>
        <img src={imageArr[data.status]} 
        style={{
            height: 70, 
            width: 70,
            marginBottom: '6%',
            marginLeft: '4%'
        }}/>
        </div>

    </div>
        );
    }
}

const styles = {
    mainDiv: {
        marginTop: '4%', 
        border: "1px solid black", 
        display: "flex",
        width: '100%',
        padding: '3%'
    }
}
