import React, { Component } from "react";
import { FaSearch} from 'react-icons/fa';
import { MdLocationOn } from 'react-icons/md';

class Home extends Component {
  state = {
      weekArr: [
          {id: 1, day: 'Sunday'},
          {id: 2, day: 'Monday'},
          {id: 3, day: 'Tuesday'},
          {id: 4, day: 'Wednesday'},
          {id: 5, day: 'Thursday'},
          {id: 6, day: 'Friday'},
          {id: 7, day: 'Saturday'}
      ],
  };

  render() {
    console.log('11111', this.state.weekArr);
    return (
      <div style={styles.mainStyle}>
        <div style={styles.boxStyle} > 
        <div style={{
            flexDirection: 'row',  
            display: 'flex', 
            //border: "1px solid black"
            }}>
        <MdLocationOn size='2em' />
            <h3 style={{paddingLeft: '16%', }}>Location</h3>
        </div>
           <FaSearch size='2em'/>
        </div>
        <div style={{display: 'flex', marginTop: '4%', flexDirection: 'row'}}>
            {
                this.state.weekArr.map((item, index) => {
                    return (
                        <ul key={item.id}>
                            <p>{item.name}</p>
                        </ul>
                    );
                })
            }
        </div>
      </div>
    );
  }
}

const styles = {
  mainStyle: {
    display: "flex",
    alignItems: 'center',
    margin: "1.6%",
    marginLeft: '20%',
    marginRight: '20%',
    flexDirection: 'column'
  },
  boxStyle: {
    display: 'flex',
    flexDirection: 'row',
    height: 80,
    borderRadius: 8,
    padding: '1.6%',
    justifyContent: 'space-between',
    boxShadow: "1px 4px 6px 1px #9E9E9E",
    width: '100%',
  }
};

export default Home;
