import React, { Component } from 'react';
import '../App.css';

const imageArr = {
    'Sunny': 'https://cdn4.iconfinder.com/data/icons/the-weather-is-nice-today/64/weather_3-512.png',
    'Cloudy': 'https://img2.pngio.com/download-free-png-partly-cloudy-the-weather-w-dlpngcom-partly-cloudy-png-492_440.png',
    'Rainfall': 'https://png.pngtree.com/png-vector/20191002/ourlarge/pngtree-cloud-with-rain-drops-icon-cartoon-style-png-image_1784624.jpg'
}

export default class SecondComponent extends Component {
    state = { 
        isActiveDay: false
     }

    componentDidMount() {
        let now = new Date();
        //console.log('222222', this.props.data[now.getDay()].day)
        //console.log('222222', now.getDay())
        if (now.getDay() === this.props.key) {
            this.setState({
                isActiveDay: true
            })
        }
    }

    render() {
        console.log('222222', this.state.isActiveDay)
        const {data, key} = this.props;
        return ( 
            <div 
            style={{
                display: 'flex',
                flexDirection: 'column',
                flex: 1,
                border: this.state.isActiveDay ? "1px solid black" : "none",
            }
            // this.state.isActiveDay ? {
            //     border: "1px solid black"
            // } : {}
        }
            >
                <ul key={key} style={{marginLeft: '15%', marginRight: '2%',  marginTop: '4%'}}>
                        <p style={{fontWeight: 'bold', fontSize: '20px'}}>{data.day}</p>
                        <div style={{display: 'flex', flexDirection: 'row',}}>
                            <p style={{marginRight: '5%', fontWeight: 'bold'}}>{data.maxTemp}&deg;</p>
                            <p>{data.minTemp}&deg;</p>
                        </div>
                        <img src={imageArr[data.status]} style={{
                                height: 40, 
                                width: 40,
                                marginBottom: '3%'
                            }}/>
                        <p>{data.status}</p>
                </ul>
                 </div>
         );
    }
}
