import React, { Component } from 'react';
import {imageArr, kelvinToCelcius} from '../constants'
import moment from 'moment'

export default class WeatherReport extends Component {
    state = {
        averageTemp:null,
        averagePressure:null,
        averageHumidity:null
    };
    componentDidUpdate (prevProps, prevState) {
        let {weatherUpdate} = this.props
        let {averagePressure, averageHumidity} = this.state;
        let pressureCount = 0; let humidityCount = 0;
        if (averagePressure == null && averageHumidity == null || weatherUpdate !== prevProps.weatherUpdate) {
            weatherUpdate && weatherUpdate.list.map((weather) => {
                humidityCount += weather.main.humidity
                pressureCount += weather.main.pressure
            })
            console.log('11111', pressureCount)
            averagePressure = parseInt(pressureCount / 24);
            averageHumidity =  parseInt(humidityCount / 24);
            this.setState({
                averagePressure: averagePressure,
                averageHumidity: averageHumidity
            })
        }

    }

    renderPerson = (item, index) => {


        let time = item.dt_txt.split(' ')[1].split(':')[0]
        let meridian = time >= 12 ? 'pm' : 'am';
        time  = time > 12 ? time - 12 : time;
        let celcius = kelvinToCelcius(item.main.temp);
        return(
            <div style={{display: 'flex', flexDirection: 'column', paddingRight: '5%', }}>
            <p style={{fontSize: 30}}>{parseInt(celcius)}&deg;</p>
            <p>{time} {meridian}</p>
        </div>
        );

    }


    render() {
        const { data, weatherUpdate} = this.props;
        const {averagePressure, averageHumidity} = this.state;
        let celcius = weatherUpdate ? parseInt(kelvinToCelcius(weatherUpdate.list[6].main.temp)) : '--';
        return (
            <div style={styles.mainDiv}>
        <div style={{
            display: 'flex', 
            flexDirection: 'row',
            width: '50%',
        }}>
        <h1 style={{fontWeight: 'bold', paddingTop: '1.5%', paddingRight: '2%'}}>{celcius}&deg; C</h1>
        <img src={imageArr[data.status]} 
        style={{
            height: 65, 
            width: 65,
            //position: 'absolute'
        }}/>
        </div>
        { weatherUpdate ? (
        <div style={{
            display: 'block', 
            flexDirection: 'row', 
            justifyContent: 'space-between',
            overflow: 'scroll', }}>
        {/* <FlatList
          list={weatherUpdate.list}
          renderItem={(item, index) => this.renderPerson(item, index)}
          renderWhenEmpty={() => <div>List is empty!</div>}
        /> */}
        {weatherUpdate.list.map((item, index) =>  this.renderPerson(item, index))}

        </div>) : null}

        <div style={{
            display: 'flex', 
            flexDirection: 'row',
            marginTop: '3%',
            justifyContent: 'space-between',
            }}> 
            <div style={{
            display: 'flex', 
            flexDirection: 'column',
            marginRight: '2%',
            backgroundColor: '#f2f2f2',
            alignItems: 'center',
            width: '50%',
            borderRadius: 4,
            }}>
              <p style={{fontWeight: 'bold', fontSize: 24}}>Pressure</p>
            <p style={{fontSize: 24}}>{averagePressure} hpa</p>
            </div>
            <div style={{
            display: 'flex', 
            flexDirection: 'column',
            alignItems: 'center',
            backgroundColor: '#f2f2f2',
            marginLeft: '2%',
            borderRadius: 4,
            width: '50%',
            }}>
              <p style={{fontWeight: 'bold', fontSize: 24}}>Humidity</p>
            <p style={{fontSize: 24}}>{averageHumidity} %</p>
            </div>
        </div>

    </div>
        );
    }
}

const styles = {
    mainDiv: {
        marginTop: '4%', 
        border: "1px solid black", 
        display: "flex",
        width: '100%',
        padding: '3%',
        flexDirection: 'column'
    }
}
