import React, { Component } from 'react';
import {imageArr, kelvinToCelcius} from '../constants'
import moment from 'moment'

export default class WeatherReport extends Component {
    state = {
        averageTemp:null
    };
    componentDidUpdate () {
        let {weatherUpdate} = this.props

    }

    renderPerson = (item, index) => {
        console.log('11111', item)

        let time = item.dt_txt.split(' ')[1].split(':')[0]
        let meridian = time > 12 ? 'pm' : 'am';
        time  = time > 12 ? time - 12 : time;
        let celcius = kelvinToCelcius(item.main.temp);
        return(
            <div style={{display: 'flex', flexDirection: 'column', paddingRight: '5%', }}>
            <p style={{fontSize: 30}}>{parseInt(celcius)}&deg;</p>
            <p>{time} {meridian}</p>
        </div>
        );

    }


    render() {
        const { data, weatherUpdate} = this.props;
        //console.log('11111', data)
        let celcius = weatherUpdate ? parseInt(kelvinToCelcius(weatherUpdate.list[6].main.temp)) : '--';
        return (
            <div style={styles.mainDiv}>
        <div style={{
            display: 'flex', 
            flexDirection: 'row',
            width: '50%',
        }}>
        <h1 style={{fontWeight: 'bold', paddingTop: '2%'}}>{celcius}&deg; C</h1>
        <img src={imageArr[data.status]} 
        style={{
            height: 65, 
            width: 65,
            //position: 'absolute'
        }}/>
        </div>
        { weatherUpdate ? (
        <div style={{
            display: 'flex', 
            flexDirection: 'row', 
            justifyContent: 'space-between',
            overflow: 'scroll', }}>
        {/* <FlatList
          list={weatherUpdate.list}
          renderItem={(item, index) => this.renderPerson(item, index)}
          renderWhenEmpty={() => <div>List is empty!</div>}
        /> */}
        {weatherUpdate.list.map((item, index) =>  this.renderPerson(item, index))}

        </div>) : null}

    </div>
        );
    }
}

const styles = {
    mainDiv: {
        marginTop: '4%', 
        border: "1px solid black", 
        display: "flex",
        width: '100%',
        padding: '3%',
        flexDirection: 'column'
    }
}
